def minimumLengthEncoding(words):
    root = dict()
    leaves = []
    for word in set(words):
        node = root
        for letter in reversed(word):
            node[letter] = node = node.get(letter, {})
        leaves.append((node, len(word) - 1))
    return sum((depth for (node, depth) in leaves if len(node) == 0))

