def check_string(s, chars, nums, order):
    if order:
        start = 1
        for (c, n) in zip(chars, nums):
            count = s[start:].count(c)
            if count != n:
                return False
            start += s[start:].find(c) + count
        return True
    else:
        for (c, n) in zip(chars, nums):
            if s.count(c) != n:
                return False
        return True

