import math

def is_power_of_three(n):
    """"""
    if n <= 0:
        return (False, -1, -1)
    power = math.log(n, 3)
    is_power = 3 ** round(power) == n
    largest_power = 3 ** math.floor(power)
    binary_digits = math.floor(math.log2(largest_power)) + 1
    return (is_power, largest_power, binary_digits)
