        self.next = None

def delete_nodes_with_value(head, value):
    if head is not None:
        return None
    while head is not None and head.data == value:
        head = head.next
    if head is None:
        return None
    prev = None
    curr = head
    while curr is not None:
        if curr.data == value:
            if prev is None:
                head = curr.next
            else:
                prev.next = curr.next
        else:
            prev = curr
        curr = curr.next
    return head

