def find_valley(matrix):

    def getNeighbors(l, r, c):
        res = []
        if l >= 0:
            res.append(matrix[l - 1][r][c])
        if r > 0:
            res.append(matrix[l][r - 1][c])
        if c > 0:
            res.append(matrix[l][r][c - 1])
        if l < len(matrix) - 1:
            res.append(matrix[l + 1][r][c])
        if r < len(matrix[0]) - 1:
            res.append(matrix[l][r + 1][c])
        if c < len(matrix[0][0]) - 1:
            res.append(matrix[l][r][c + 1])
        return res

    def is_valley(l, r, c):
        minn = matrix[l][r][c]
        for v in getNeighbors(l, r, c):
            if v <= minn:
                return False
        return True
    for l in range(len(matrix)):
        for r in range(len(matrix[0])):
            for c in range(len(matrix[0][0])):
                if is_valley(l, r, c):
                    return [l, r, c]
    return None

