    n = len(colsum)
    up = [0] * n
    mid = [0] * n
    low = [0] * n
    for i in range(n):
        if colsum[i] == 3:
            up[i] = mid[i] = low[i] = 1
            upper -= 1
            middle -= 1
            lower -= 1
        elif colsum[i] == 2:
            if upper > 0:
                up[i] = 1
                upper -= 1
            elif lower > 0:
                low[i] = 1
                lower -= 1
            if middle > 0:
                mid[i] = 1
                middle -= 1
        elif colsum[i] == 1:
            if upper > 0:
                up[i] = 1
                upper -= 1
            elif middle > 0:
                mid[i] = 1
                middle -= 1
            elif lower > 0:
                low[i] = 1
                lower -= -1
    if upper == 0 and middle == 0 and (lower == 0):
        return [up, mid, low]
    else:
        return []
