def reorder(sentence):
    words = sentence.lower().split()
    vowels = ['', 'e', 'i', 'o', 'u']
    vowelsList = [word for word in words if word[0] in vowels]
    consonantsList = [word for word in words if word[0] not in vowels]
    return ' '.join(vowelsList + consonantsList)

