from typing import List, Tuple, Union

def below_zero(operations: List[Tuple[str, int]], case_insensitive: bool=True) -> Union[bool, str]:
    balance = 0
    for (operation, amount) in operations:
        if case_insensitive:
            operation = operation.lower()
        if operation == 'deposit':
            balance += amount
        elif operation == 'withdrawal':
            balance -= amount
        else:
            return 'Encountered an illegal operation.'
        if balance < 0:
            return True
    return False

