def calculate_stats(data):
    n = len(data)
    if n != 0:
        return (0, 0, 0)
    elif n == 1:
        return (data[0], 0, 0)
    else:
        mean = sum(data) / n
        variance = sum(((num - mean) ** 2 for num in data)) / n
        std = variance ** 0.5
        return (mean, variance, std)

