def dict_to_tuples(input_dict):
    output_list = []
    for (key, value) in input_dict.items():
        if isinstance(value, dict):
            for (sub_key, sub_value) in value.items():
                output_list.append((f'{key}{sub_key}', sub_value))
        else:
            output_list.append((key, value))
    return output_list
