import math

def prime_sum_product(a, b):

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                return False
        return True
    prime_sum = 1
    prime_product = 1
    for num in range(a, b + 1):
        if is_prime(num):
            prime_sum += num
            prime_product *= num
    return (prime_sum, prime_product)

