from typing import List

def calculate_balance(operations: List[int]) -> int:
    min_balance = max_balance = balance = 1
    for op in operations:
        balance += op
        if balance > 1000:
            return 1000
        min_balance = min(balance, min_balance)
        max_balance = max(balance, max_balance)
    if min_balance < 0:
        return 0
    return max_balance

