def count_unique_pairs(nums, target_sum):
    if len(nums) <= 2:
        return 0
    nums = sorted(nums)
    pairs = set()
    left = 0
    right = len(nums) - 1
    while left < right:
        current_sum = nums[left] + nums[right]
        if current_sum == target_sum:
            pairs.add((nums[left], nums[right]))
            left += 1
            right -= 1
        elif current_sum < target_sum:
            left += 1
        else:
            right -= 1
    return len(pairs)

