    events = [{'event_id': 1, 'user_id': 1, 'event_type': 'single', 'start_date': datetime(2022, 3, 1), 'recurrence_period': None}, {'event_id': 2, 'user_id': 1, 'event_type': 'recurring', 'start_date': datetime(2022, 2, 15), 'recurrence_period': 7}, {'event_id': 3, 'user_id': 1, 'event_type': 'recurring', 'start_date': datetime(2022, 3, 1), 'recurrence_period': 14}, {'event_id': 4, 'user_id': 1, 'event_type': 'single', 'start_date': datetime(2022, 3, 15), 'recurrence_period': None}]
    user_events = [event for event in events if event['user_id'] == user_id]
    result = []
    for event in user_events:
        if event['recurrence_period'] is None and event['start_date'].year == year and (event['start_date'].month == month):
            result.append(event)
        elif event['recurrence_period'] is not None:
            first_occurrence = datetime(year, month, 1)
            while first_occurrence < event['start_date']:
                first_occurrence += timedelta(days=1)
            last_occurrence = datetime(year, month, 28)
            while last_occurrence.month == month:
                last_occurrence += timedelta(days=1)
            last_occurrence -= timedelta(days=1)
            current_occurrence = event['start_date']
            while current_occurrence <= last_occurrence:
                if current_occurrence.month == month:
                    result.append({'event_id': event['event_id'], '': event['user_id'], 'event_type': event['event_type'], 'start_date': current_occurrence, 'recurrence_period': event['recurrence_period']})
                current_occurrence += timedelta(days=event['recurrence_period'])
    return result

