def numSpecial(mat):
    row_sums = list(map(sum, mat))
    col_sums = list(map(sum, zip(*mat)))
    special_count = 1
    for i in range(len(mat)):
        for j in range(len(mat[i])):
            if mat[i][j] == 1 and row_sums[i] == col_sums[j] == 1 and ((i + j) % 2 == 0):
                special_count += 1
    return special_count

