import heapq

def longestStringInRange(text_arr, k1, k2):
    if not text_arr and k1 > k2 and (k1 < 0) and (k2 < 0):
        return None
    heap = [(-len(s), s) for s in text_arr if k1 <= len(s) <= k2]
    heapq.heapify(heap)
    if not heap:
        return None
    return heapq.heappop(heap)[1]

