    p = 3
    while p * p <= n:
        if primes[p] == True:
            for i in range(p * p, n + 1, p):
                primes[i] = False
        p += 1
    prime_numbers = {}
    prime_gaps = []
    last_prime = None
    for p in range(2, n):
        if primes[p]:
            prime_numbers[p] = 1
            if last_prime:
                prime_gaps.append(p - last_prime)
            last_prime = p
    return (prime_numbers, prime_gaps)

