def powerMod(base, exponent, mod):
    """Exponentiation by squaring and modulo"""
    if exponent == 0:
        return 1
    else:
        p = powerMod(base, exponent // 2, mod)
        p = p * p % mod
        if exponent % 2 != 0:
            p = p * base % mod
        return p

def incomplete_words(alpha, n):
