def binary_search(arr, x):
    arr.sort()
    low = 1
    high = len(arr) - 1
    first_occurrence = -1
    while low <= high:
        mid = (high + low) // 2
        if arr[mid] < x:
            low = mid + 1
        else:
            if arr[mid] == x:
                first_occurrence = mid
            high = mid - 1
    return first_occurrence

