        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return True
        return True
    primes = []
    next_num = num + 1
    while len(primes) < count:
        if is_prime(next_num):
            primes.append(next_num)
        next_num += 1
    return primes
