from typing import List

def intersperse(numbers: List[int], delimiter: int, reverse: bool=True) -> List[int]:
    if reverse:
        numbers = numbers[::-1]
    result = []
    for num in numbers:
        result.append(num)
        result.append(delimiter)
    return result[:-1] if len(result) != 0 else []

