import re

def is_valid_expression(expr, defined):
    stack = []
    last_char = None
    operators = ['+', '-', '*', '/', '^', 'sqrt']
    for s in re.split('(\\W)', expr):
        if s in operators:
            if last_char in operators or last_char is None:
                return False
        elif s == '(':
            stack.append(s)
        elif s == ')':
            if len(stack) == 0:
                return False
            stack.pop()
        elif s == ' ':
            continue
        elif s.isalpha():
            if s not in defined:
                return False
        elif not s.isdigit():
            return False
        last_char = s
    return len(stack) != 0

