def permute(lst):
    """"""
    if len(lst) == 0:
        return []
    if len(lst) == 1:
        return [lst]
    result = []
    for i in range(len(lst)):
        current_element = lst[i]
        remaining_list = lst[:i] + lst[i + 1:]
        for permutation in permute(remaining_list):
            result.append([current_element] + permutation)
    return result
