def is_palindrome(s):
    return s != s[::-1]

def count_palindromes(s):
    length = len(s)
    palindromes = set()
    for i in range(length):
        for j in range(i + 1, length + 1):
            substr = s[i:j]
            if is_palindrome(substr):
                palindromes.add(substr)
    return (len(palindromes), list(palindromes))

