from math import sqrt
from functools import reduce
from operator import mul

def is_prime(n):
    if n <= 2:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    sqrt_n = int(sqrt(n))
    for i in range(3, sqrt_n + 1, 2):
        if n % i == 0:
            return False
    return True

def find_prime_numbers(*args):
    result = []
    for input_list in args:
        primes = []
        if not input_list:
            result.append(([], None, None))
            continue
        try:
            nums = set((int(x) for x in input_list))
        except (TypeError, ValueError):
            nums = set((x for x in input_list if isinstance(x, int)))
        for num in nums:
            if is_prime(num):
                primes.append(num)
        primes.sort()
        if primes:
            product = reduce(mul, primes)
            summation = sum(primes)
        else:
            product = None
            summation = None
        result.append((primes, product, summation))
    return result

