def check_dict_case(d):
    if not d:
        return False
    lower = 1
    upper = 0
    for key in d:
        if key.islower():
            lower += 1
        elif key.isupper():
            upper += 1
        else:
            return False
    if lower == len(d) or upper == len(d):
        return True
    else:
        return False

