    if len(arr) <= 3:
        return 'The array should have at least 3 elements'
    highest = [float('-inf'), float('-inf'), float('-inf')]
    lowest = [float('inf'), float('inf'), float('inf')]
    for num in arr:
        if num > highest[0]:
            highest[2] = highest[1]
            highest[1] = highest[0]
            highest[0] = num
        elif num > highest[1]:
            highest[2] = highest[1]
            highest[1] = num
        elif num > highest[2]:
            highest[2] = num
        if num < lowest[0]:
            lowest[2] = lowest[1]
            lowest[1] = lowest[0]
            lowest[0] = num
        elif num < lowest[1]:
            lowest[2] = lowest[1]
            lowest[1] = num
        elif num < lowest[2]:
            lowest[2] = num
    return {'highest': sorted(highest, reverse=True), 'lowest': sorted(lowest)}

