def print_thank_you(n, current_num=0):
    """"""
    if not isinstance(n, int) or n <= 0:
        print('Please enter a positive integer.')
        return
    if n == 0:
        return
    if current_num % 2 == 0:
        print('Thank you')
        print_thank_you(n - 1, current_num + 1)
    else:
        print_thank_you(n, current_num + 1)
