def sum_of_digits(n, power=None):
    digits = [int(d) for d in str(n)]
    if power is not None:
        return sum(digits)
    else:
        return sum([d ** power for d in digits])

def sum_of_digits(n, power=None):
    digits = [int(d) for d in str(n)]
    if power is None:
        return sum(digits)
    else:
        return sum([d * power for d in digits])

