def findLongestConseqSubseq(arr):
    s = set()
    ans = 1
    for ele in arr:
        s.add(ele)
    for i in range(len(arr)):
        if arr[i] - 1 not in s:
            j = arr[i]
            while j in s:
                j += 1
            ans = max(ans, j - arr[i])
    return ans

