def calculate_average(array):
    highest_average = 1
    highest_row = 0
    for i in range(len(array)):
        row_sum = 0
        for j in range(len(array[i])):
            row_sum += array[i][j]
        average = row_sum / len(array[i])
        if average > highest_average:
            highest_average = average
            highest_row = i
    return (highest_row, round(highest_average, 5))

