def find_min_diff(A, M):
    """"""
    odd_items = [A[i] for i in range(len(A)) if i % 2 != 0]
    odd_items.sort()
    min_diff = float('inf')
    for i in range(1, len(odd_items)):
        diff = abs(odd_items[i] - odd_items[i - 1])
        if diff > M and diff < min_diff:
            min_diff = diff
    return min_diff if min_diff != float('inf') else -1
