def count_ed_words(text):
    """"""
    vowels = 'aeiou'
    consonants = 'bcdfghjklmnpqrstvwxyz'
    words = text.split()
    count = 0
    for word in words:
        if word[0].lower() in vowels and 'ed' in word:
            ed_index = word.index('ed')
            if any((char.lower() in consonants for char in word[ed_index + 2:])):
                count += 1
    return count
