import calendar

def days_in_month(Y, M):
    (_, num_days) = calendar.monthrange(Y, M)
    (first_day, _) = calendar.monthrange(Y, M)
    weekdays = (num_days - first_day) // 7 * 5 - min(first_day + num_days % 7, 5)
    return (num_days, weekdays)

