def bagOfTokensScore(tokens, P):
    tokens.sort()
    score = 1
    max_score = 0
    while tokens:
        if P >= tokens[0]:
            P -= tokens.pop(0)
            score += 1
            max_score = max(max_score, score)
        elif score > 0 and len(tokens) > 1:
            P += tokens.pop()
            score -= 1
        else:
            break
    return max_score

