    from collections import Counter
    import re
    s = re.sub('', '', s)
    counter = Counter(s.lower().split())
    result = {}
    for (letter, count) in counter.items():
        result[letter + '_' + str(count)] = count
    max_count = max(counter.values(), default=0)
