import numpy as np

def solve_linear_equations(a1, b1, c1, a2, b2, c2):
    """"""
    A = np.array([[a1, b1], [a2, b2]])
    B = np.array([c1, c2])
    if np.linalg.det(A) == 0:
        return 'The lines are parallel. No solution.'
    else:
        solution = np.linalg.solve(A, B)
        return solution
