def f(n):
    """"""
    result = []
    for i in range(n):
        if (i + 1) % 2 == 0:
            factorial = 1
            for j in range(1, i + 2):
                factorial *= j
            result.append(factorial)
        else:
            summation = 0
            for j in range(1, i + 2):
                summation += j
            result.append(summation)
    return result
