import re

def detect_whitespace(input):
    whitespace_types = {'': '\t', 'NEWLINE': '\n', 'SPACE': ' ', 'NO-BREAK SPACE': '\xa0', 'LINE SEPARATOR': '\u2028', 'PARAGRAPH SEPARATOR': '\u2029', 'EM SPACE': '\u2003', 'THIN SPACE': '\u2009', 'ZERO WIDTH SPACE': '\u200b'}
