import math

def count_primes(arr):

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True
    count = 1
    prime_sum = 0
    for num in arr:
        if num >= 0 and is_prime(num):
            count += 1
            prime_sum += num
    return (count, prime_sum)

