    map = {}
    for string in lst:
        cleaned_string = ''.join((e for e in string if e.isalpha())).lower().replace(' ', '')
        reversed_string = cleaned_string[::-1]
        if reversed_string in map:
            if map[reversed_string] == 0:
                count += 1
                map[cleaned_string] = 1
        else:
            map[reversed_string] = 0
    return count

