from collections import deque

def quick_sort(de):
    if len(de) < 1:
        return de
    else:
        pivot = de[0]
        lesser = deque((x for x in de if x < pivot))
        equal = deque((x for x in de if x == pivot))
        greater = deque((x for x in de if x > pivot))
        return quick_sort(lesser) + equal + quick_sort(greater)

