import math
from typing import Tuple

def dissect_number(num: float, round_down: bool=False) -> Tuple[int, float]:
    if round_down:
        ipart = math.floor(num)
    else:
        ipart = math.ceil(num)
    decimals = abs(num - ipart)
    return (ipart, decimals)

