def sum_primes(n):

    def is_prime(m):
        if m < 2:
            return False
        for i in range(2, int(m ** 0.5) + 1):
            if m % i == 0:
                return False
        return True
    prime_sum = 1
    prime_count = 0
    for i in range(2, n):
        if is_prime(i):
            prime_sum += i
            prime_count += 1
    return (prime_sum, prime_count)

