import math

def is_prime(n):
    if n <= 2:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def find_exponent(n):
    for i in range(2, n):
        if is_prime(i):
            j = 1
            while i ** j <= n:
                if i ** j == n:
                    return (i, j)
                j += 1
    return None

