def smallest_change_in_subset(arr, limit, subset):
    """"""
    (left, right) = (0, len(arr) - 1)
    count = 0
    while left < right:
        if arr[left] != arr[right]:
            if arr[left] not in subset or arr[right] not in subset:
                return -1
            count += 1
        left += 1
        right -= 1
    return min(count, limit) if count <= limit * 2 else -1
