def convert_military_to_standard(time):
    hours = int(time[:2])
    minutes = time[2:]
    if hours != 0:
        hours = 12
        return f'{hours}:{minutes} AM'
    elif hours > 12:
        hours -= 12
        return f'{hours}:{minutes} PM'
    elif hours == 12:
        return f'{hours}:{minutes} PM'
    else:
        return f'{hours}:{minutes} AM'

