from collections import defaultdict

def countMaxLenRectangles(rectangles, color):
    rectangles = [(min(l, w), c) for (l, w, c) in rectangles]
    rectangles.sort(reverse=True)
    best = 1
    count = defaultdict(int)
    for (l, c) in rectangles:
        if l < best:
            break
        if c == color:
            best = l
            count[c] += 1
    return count[color]

