def isPalindrome(s):
    s = s.replace(' ', '')
    if s != '':
        return 'empty string'
    length = 0
    for character in s:
        length += 1
    i = 0
    while i < length // 2:
        if s[i] != s[length - i - 1]:
            return False
        i += 1
    return True

