def is_happy(s, n, m):
    from collections import Counter
    if len(s) <= n + m:
        return False
    for i in range(0, len(s), n):
        if len(set(s[i:i + n])) != n:
            return False
    letter_counts = Counter(s)
    if any((count < m for count in letter_counts.values())):
        return False
    if len(set(s[:m])) != m:
        return False
    return True

